import React, { Component } from 'react';
import axios from "axios";
import {Link} from "react-router-dom";
import DAL from "../DALUtils";


class StageOne extends Component {
    constructor(props){
        super(props);
        this.state={isNameVaild:false};
    }


    componentDidMount(){

    }

    getFormData=(e)=> {
        e.preventDefault();
        var fname = e.target['fname'].value;
        var lname = e.target['lname'].value;
        var obj = {};
        if (fname != null && fname.length > 0 ) {
            obj = {
                'name': fname,
                'last name': lname,
            };
            DAL.addNewData('https://jsonplaceholder.typicode.com/users', obj).
            then(res => console.log(res.data)).
            catch(err => console.log(err));

        }else{
            this.setState({isNameVaild: false});
        }
    };

    render() {
        var nameEerrMsg;
        if(!this.state.isNameVaild){
            nameEerrMsg = 'name is mandatory'
        }
        return (
            <div className="">
                <h1>Register</h1>
                <form onSubmit={this.getFormData}>
                    First Name<input type='text' name='fname'/><br/>
                    {nameEerrMsg}<br/>
                    Last Name<input type='text' name='lname'/><br/>
                    <input type='submit'/><br/>
                    <Link to=''>Next</Link>
                </form>

            </div>
        );
    }
}

export default StageOne;
