import React, { Component } from 'react';
import './posts.css';
import DAL from "../DALUtils";
import {connect} from 'react-redux';
import Todo from "./Todo";

class Posts extends Component {
    constructor(props){
        super(props);
        this.state={todos:[]};
    }


    componentDidMount(){
        DAL.getUsers('https://jsonplaceholder.typicode.com/posts').
        then(res => this.props.dispatch({type:'GET-ALL-POSTS' , todos:res.data})).
        catch(err => console.log(err));
    }

    static getDerivedStateFromProps(nextProps,nextState){
        console.log("Post-sgetDerivedStateFromProps");
        return{todos:nextProps.data.todos}
    }



    render() {
        var postsMap= this.state.todos.map((item,index)=>{
            return<div className='postMapping' key={index}>
                <p>Posts - User {item.userId}</p>
                <input type='button' value='Add'/>
                <div>
                    <Post title={item.title} body={item.body}/>
                </div>
            </div>
        });

        return (
            <div id="Posts">
                {postsMap}
            </div>
        );
    }


}


const mapStateToProps = (state)=> {
    console.log("state Posts",state);
    return {
        data: state
    }
};


export default connect(mapStateToProps)(Posts);
