import React, { Component } from 'react';
import axios from "axios";
import Todos from "../MainTasks/Todos/Todos";
import {Switch,Route,Link} from 'react-router-dom';

class User extends Component {
    constructor(props){
        super(props);
        this.state={ activeUser:"",userId:this.props.userId, name: this.props.name, email: this.props.email, street:this.props.street, city:this.props.city, zipcode:this.props.zipcode};
    }

    static getDerivedStateFromProps(nextProps,nextState){
        console.log("user-getDerivedStateFromProps");
        console.log("nextProps.activeUser",nextProps.activeUser);
        console.log("nextState.activeUser",nextState.activeUser);
        if(nextState.userId != nextProps.userId){
            return{activeUser:nextProps.activeUser,userId:nextProps.userId, name:nextProps.name, email:nextProps.email, street:nextProps.street, city:nextProps.city, zipcode:nextProps.zipcode}
        }else{
            console.log("in else getDrived");
            return{activeUser:nextState.activeUser,userId:nextState.userId, name:nextState.name, email:nextState.email, street:nextState.street, city:nextState.city, zipcode:nextState.zipcode}
        }
    }

    componentDidMount(){}

    active=()=>{
        if( this.state.active.length <= 0){
            this.setState({active:"active"});
        }else{
            this.setState({active:""});
        }
    };

    showOtherDataOnHover=()=>{
        this.setState({active:"active"});
    };

    updateUserDetails=(e)=>{
        var inputName = e.target.name;
        var e = e.target.value;
        switch (inputName){
            case 'name':
                console.log(e, "e");
                this.setState({name:e});
                break;
            case 'email':
                this.setState({email:e});
                break;
            case 'street':
                this.setState({street:e});
                break;
            case 'city':
                this.setState({city:e});
                break;
            case 'zipcode':
                this.setState({zipcode:e});
                break;
            default:
                console.log("not matched");

        }
    };

    updateUserInArray=()=>{
        var thisUserId = this.props.userId;
        var NewUserName = this.state.name;
        var NewUserEmail = this.state.email;
        var NewUserStreet = this.state.street;
        var NewUserCity = this.state.city;
        var NewUserZipcode = this.state.zipcode;

        this.props.onUpdate(thisUserId,NewUserName,NewUserEmail,NewUserStreet,NewUserCity,NewUserZipcode)
    };

    deleteUserInArray=()=>{
        var thisUserId = this.props.userId;
        this.props.onDelete(thisUserId);

    };

    userColorActive=(x,next)=>{

        console.log("this.props.userId",this.props.userId);
        console.log("this.state.activeUser",this.state.activeUser);
        var x = this.x;
        console.log("x-id",x);
        this.setState({activeUser:x});
        console.log("this.state.activeUser22",this.state.activeUser);

    };

    render() {
        return (
            <div id="User" className={`white-bg ${this.state.activeUser === this.props.userId ? 'grey-bg' : ''}`} >
                <div className="headlineDiv">
                    <p className='p-id'>ID : <b>{this.state.userId}</b></p>
                    <Link to={`/tasks/${this.props.userId}`} className='btn-showMissions' onClick={this.userColorActive.bind(this.props.userId ,next)}/>
                </div>
                <p>Name :</p><input type='test' name="name" value={this.state.name} onChange={this.updateUserDetails}/><br/>
                <p>Email :</p><input type='text' name="email" value={this.state.email} onChange={this.updateUserDetails}/><br/>
                <input  type='button' className='user-btn btn-other' value='Other Data' onMouseEnter={this.showOtherDataOnHover} onClick={this.active}  />
                <div id='otherData' className={this.state.active}>
                    <p>Street :</p><input type='text' name="street" value={this.state.street} onChange={this.updateUserDetails}/><br/>
                    <p>City :</p><input type='text' name="city" value={this.state.city} onChange={this.updateUserDetails}/><br/>
                    <p>Zipcode :</p><input type='text' name="zipcode" value={this.state.zipcode} onChange={this.updateUserDetails}/><br/>
                </div>
                <input  type='button' className='user-btn btn-update' value='Update' onClick={this.updateUserInArray}/>
                <input  type='button' className='user-btn btn-delete' value='Delete' onClick={this.deleteUserInArray}/>
            </div>
        );
    }
}


export default User;
