import React, { Component } from 'react';
import axios from "axios";
import {Link} from "react-router-dom";
import DAL from "../DALUtils";


class StageOne extends Component {
    constructor(props){
        super(props);
        this.state={isNameTrue:true ,objData:{}};
    }

    static getDerivedStateFromProps(nextProps){
        return {objData: nextProps.match.params.objData}
    }

    componentDidMount(){}

    getFormData=(e)=> {
        console.log('innnnnnn');
        e.preventDefault();
        var fname = e.target['fname'].value;
        var lname = e.target['lname'].value;
        var obj={};
        if (fname != null && fname.length > 0 ) {
            obj = {
                'name': fname,
                'last name': lname,
            };

            localStorage.setItem('myData',JSON.stringify(obj));
            var getDataFromStorage = localStorage.getItem('myData');
            console.log('getDataFromStorage:', JSON.parse(getDataFromStorage));
            this.props.history.push('/stage2/'+this.state.objData);


        }else{
            this.setState({isNameTrue: false});
        }
    };

    render() {
        var nameErorMsg;
        if(!this.state.isNameTrue){
            nameErorMsg = 'name is mandatory'
        }
        return (
            <div className="">
                <h1>Register</h1>
                <form onSubmit={this.getFormData}>
                    First Name<input type='text' name='fname'/><br/>
                    {nameErorMsg}<br/>
                    Last Name<input type='text' name='lname'/><br/>
                    <input type='submit' value='Next'/>
                </form>

            </div>
        );
    }
}

export default StageOne;
