import React, { Component } from 'react';


class StageThree extends Component {

    constructor(props){
        super(props);
        this.state={newObj:'Hifa',arr:[]}
    }

    static getDerivedStateFromProps(nextProps){
        var getDataFromStorage = localStorage.getItem('myData');
        var newDataFromStorage = JSON.parse(getDataFromStorage);

        console.log(newDataFromStorage, 'new Data From Storage form step 2');
        console.log(typeof(newDataFromStorage), 'type  new Data');
        console.log(typeof(getDataFromStorage), 'type  get Data');
        console.log(getDataFromStorage, 'get Data From Storage form step 2');
        var arr=[getDataFromStorage];
        arr.push({number:4});
        console.log(arr);
        this.setState({arr:arr});
        return newDataFromStorage;
    }

    componentDidMount(){}

    componentDidUpdate(){
        return true;
    }

    getCity=(e)=>{
        var chosenCity = e.target.value;
        var newObj=chosenCity;
        this.setState({newObj:newObj});

    };
    saveStorage=()=>{
        console.log(this.state.newObj , 'my new obj');
        console.log(this.state.newDataFromStorage, 'new Data From Storage form step 2');
        /*this.state.newDataFromStorage['city'] = this.state.newObj;*/
        this.state.arr.push(this.state.newObj);
        console.log(this.state.newDataFromStorage , 'new data From Storage');
        localStorage.setItem('myData',JSON.stringify(this.state.newDataFromStorage));
    };

    render() {

        return (
            <div className="">
                <p>Chose City</p><br/>
                <select name='city' onChange={this.getCity}>
                    <option value="Hifa">Hifa</option>
                    <option value="TLV">Tel Aviv</option>
                    <option value="Hadera">Hadera</option>
                </select>
                <br/>
                <input type='button' value='Next' onClick={this.saveStorage}/>
            </div>
        );
    }
}

export default StageThree;
