const mainReducer = (
    state = {counter:0 ,userArr:[]}, actionData) =>
{
    switch (actionData.type)
    {
        case 'INCREMENT':
        {
            return{counter:state.counter + actionData.delta}
        }
        case  'DECREMENT' :
        {
            return{counter:state.counter - actionData.delta}
        }
        case 'ADD-USER':
        {
            var arr = state.userArr;
            arr.push(actionData.user);
            return{userArr:arr}
        }
        case 'DELETE':{
            var arr = state.userArr;
            arr.push(actionData.user);
            return{userArr:arr}
        }

        default:
            return state
    }
};

export default mainReducer;
