const mainReducer = (
    state = {counter:0 , userArr:[]}, actionData) =>
{
    switch (actionData.type)
    {
        case 'INCREMENT':
        {
            return{counter:state.counter + actionData.delta}
        }
        case  'DECREMENT' :
        {
            return{counter:state.counter - actionData.delta}
        }
        case 'USER':
        {
            return{userArr:state.userArr}
        }

        default:
            return state
    }
};

export default mainReducer;
