import {connect} from "react-redux";

const mainReducer = (
    state = {users:[], todos:[],posts:[],clickedUserId:"",usersDone:false,todosDone:false},  actionData) =>
{
    switch (actionData.type)
    {

        /***TODOS***/
        case 'GET-ALL-TODOS':{
            console.log("actionData todos-GET-ALL-TODOS",actionData);
            return Object.assign({},state,
                ...state.todos,
                {todos:actionData.todos,todosDone:true}
            );
        }

        case 'GET-TODOS':{
            console.log("GET-TODOS state.todos",state.todos);
            console.log("GET-TODOS state.todos",actionData.todoId);
            var todoId = actionData.todoId;

            if(todoId > 0 ){
                var todos = state.todos;

                var thisIdArr= todos.filter(x=>x.userId === todoId).map((item,index)=>{
                    if(item.completed === false){
                        return false;
                    }
                });

                console.log("thisIdArr",thisIdArr)
            }

            return Object.assign({},state,
                ...state.users,
                {todos:state.todos ,todosDone:true}
            );
        }

        /***USERS***/
        case 'GET-ALL-USERS':{
            console.log('actionData-GET-ALL-USERS',actionData);
            var users = actionData.users;
            var todos = state.todos;
            var completedArr= todos.map((item,index)=>{
                if(item.completed === false) {
                    console.log("in if");
                    var todoUserId = item.userId;
                    console.log("todoUserId",todoUserId);
                    function filterByID(x) {
                        if(x => x.userId === todoUserId){
                            console.log("innnnnn");
                            console.log( users[item]);
                            users[item].push("completed", "true");
                            return true;
                        }
                        console.log("not inn");
                    }
                    var filtersdUsers = users.filter(filterByID);

                    console.log("filtersdUsers",filtersdUsers);
                    users[item].push("completed", "true");
                    console.log("users",users)

                }

            });
            console.log("completedArr",completedArr);

            return Object.assign({},state,
                ...state.users,
                {users:actionData.users ,usersDone:true}
            );
        }

        case 'GET-USERS':{
            console.log("state.users state.users state.users",state.users);
            return Object.assign({},state,
                ...state.users,
                {users:state.users ,usersDone:true}
            );
        }

        case 'ADD-USER':
        {
            var arr = state.userArr;
            arr.push(actionData.user);
            return{userArr:arr}
        }
        case 'DELETE':{
            var arr = state.users;
            arr.push(actionData.user);
            return{users:arr}
        }

        /***POSTS***/
        case 'GET-ALL-POSTS':{
            console.log("actionData posts-GET-ALL-POSTS",actionData);
            return Object.assign({},state,
                ...state.posts,
                {posts:actionData.posts}
            );

        }

        default:
            return state
    }
};

export default mainReducer;
