import React, { Component } from 'react';
import ChildComp from "./ChildComp";

class ParentComp extends Component {
    constructor(props){
        super(props);
        this.state= {data: "hello" , newText:""}
    }

    changeChildeData=()=>{
        this.setState({data:"no longer hello"});
    };

    getLiveText=(e)=>{
        var newText =  e.target.value;
        this.setState({newText:newText});
    };

    myCallback=(e)=>{
      var newName = e.target.name;
      var newAge = e.target.age;
      var newCity = e.target.city;
      var newAdult = e.target.adult;
    };

    render() {
        return (
            <div className={''}>
                <h1>im Parent</h1>
                <input type="button" onClick={this.changeChildeData} value="change child data"/>
                <br/>
                <input type="text" onChange={this.getLiveText}/> change my child comp live text
                <ChildComp dataFromParennt={this.state.data} textFromParent={this.state.newText} getChildeProps={this.myCallback} />
              <p></p>
            </div>
        );
    }
}

export default ParentComp;
